import React, { useContext, useEffect } from "react";
import { Col, ListGroup, Row } from "react-bootstrap";
import "./Sidebar.css";
import { useSelector } from "react-redux";
import { AppContext } from "../context/appContext";
import { addNotifications, resetNotifications } from "../features/userSlice";

const Sidebar = () => {
  const user = useSelector((state) => state.user);
  const { socket, setMembers, members, setCurrentRoom, setRooms, privateMemberMsg, rooms, setPrivateMemberMsg, currentRoom } = useContext(AppContext);

  const joinRoom = (room, isPublic = true) => {
    if(!user){
      return alert("Please login");
     }
     socket.emit("join-room" ,room);
     setCurrentRoom(room);

     if(isPublic) {
       setPrivateMemberMsg(null);
     }

     // dispatch for notifications
     dispatch(resetNotifications(room));

  }

  socket.off("notifications").on("notifications", (room) => {
    if (currentRoom != room) dispatch(addNotifications(room));
});

  useEffect(() => {
    if (user) {

        setCurrentRoom("general");
        getRooms();
        socket.emit("join-room", "general");
        socket.emit("new-user");
    }
  }, []);

  socket.off("new-user").on("new-user", (payload) => {
    setMembers(payload);
  });

  function orderIds(id1, id2) {
    if (id1 > id2) {
        return id1 + "-" + id2;
    } else {
        return id2 + "-" + id1;
    }
}


  const hanldePrivateMenberMsg = (member) => {
    setPrivateMemberMsg(member);
    const roomId = orderIds(user._id, member._id);
        joinRoom(roomId, false);

  }




function getRooms() {
  fetch("http://localhost:5001/rooms")
      .then((res) => res.json())
      .then((data) => setRooms(data));
}

if (!user) {
  return <></>;
}
  return (
    <>
       <h2>Available rooms</h2>
       <ListGroup>
       {rooms.map((room, idx) => (
         <ListGroup.Item key={idx} onClick={() => joinRoom(room) } active={room == currentRoom} style={{ cursor: "pointer", display: "flex", justifyContent: "space-between" }}>
           {room} {currentRoom !== room && <span></span>}</ListGroup.Item>
           ))}
       </ListGroup>
       <h2>Membars</h2>
       <ListGroup>
       {members.map((member) => (
         <ListGroup.Item key={member._id} style={{ cursor: "pointer"}} active={privateMemberMsg?._id === member?._id} onClick={() => hanldePrivateMenberMsg(member)} disabled={member._id === user._id}>
           {member.name}
         </ListGroup.Item>

       ))}
        </ListGroup>

    </>
  )
}

export default Sidebar
